mapper.select:
		select b.* from (select  rownum rn, a.* from (
        select * from offlineedu


			    EDU_ADR=#{edu_adr}


			    AND ( UPPER(EDU_TITLE) LIKE '%' || UPPER(#{edu_title}) || '%'
				OR UPPER(EDU_CONTENT) LIKE '%' || UPPER(#{edu_title}) || '%' )


         order by edu_no desc
         ) a ) b where rn between #{start} and #{end}

mapper.update:
		UPDATE OFFLINEEDU

 EDU_TITLE=#{edu_title},
 EDU_DATE=#{edu_date},
 EDU_TIME=#{edu_time}
 EDU_ORGAN=#{edu_organ},
 EDU_CHECK=#{edu_check},
 EDU_PERSON=#{edu_person}
 EDU_PHONE=#{edu_phone},
 EDU_FILENAME=#{edu_filename},
 EDU_ADR=#{edu_adr}

		WHERE EDU_NO = #{edu_no}

mapper.select:
SELECT COUNT(*) FROM OFFLINEEDU
EDU_ADR=#{edu_adr}
AND ( UPPER(EDU_TITLE) LIKE '%' || UPPER(#{edu_title}) || '%' OR UPPER(EDU_CONTENT) LIKE '%' || UPPER(#{edu_title}) || '%' )
