mapper.insert:
	 		INSERT INTO CROP
	 		VALUES (
	 				( select nvl(max(crop_no),0)+1 from crop )
	 				,#{user_id}
	 				,#{crop_name}	 		
	 		)

		
        		select nvl(max(fdiary_no),0)+1 from farmdiary     
    	  
			INSERT INTO FARMDIARY
							(
								 FDIARY_NO
								,FDIARY_DAY
								,FDIARY_STIME
								,FDIARY_ETIME
								,FDIARY_WORKER
								,FDIARY_WEATHER
								,FDIARY_CONTENT
								,FDIARY_FILENAME
								,USER_ID
								,CROP_NO
							)						  
			VALUES (
					#{fdiary_no}
					,#{fdiary_day} 
					,#{fdiary_stime}
					,#{fdiary_etime}
					,#{fdiary_worker}
					,#{fdiary_weather} 
					,#{fdiary_content}
					,#{fdiary_filename}
					,#{user_id}
					,#{crop_no}
					)

mapper.update:
UPDATE FARMDIARY
				SET 
				FDIARY_STIME = #{fdiary_stime}
				,FDIARY_ETIME = #{fdiary_etime}
				,FDIARY_WORKER = #{fdiary_worker}
				,FDIARY_WEATHER = #{fdiary_weather}
				,FDIARY_CONTENT = #{fdiary_content}
				,FDIARY_FILENAME = #{fdiary_filename}
WHERE fdiary_no = #{fdiary_no}
