mapper.insert:

	INSERT INTO FARMER (
						FARMER_NO
						,FARMER_FILENAME
						,USER_ID
						,FARMER_DATE
						)
	
	VALUES (
			(select nvl(max(farmer_no),0)+1 from farmer)
			,#{farmer_filename}
			,#{user_id}
			,sysdate
			)

mapper.update:
    
     UPDATE FARMER
     SET FARMER_CHECK=#{farmer_check}
     WHERE FARMER_NO=#{farmer_no}
