mapper.insert:


   SELECT NVL(MAX(INTO_NO),1)+1 FROM INTOFARM

INSERT INTO INTOFARM 
         ( INTO_NO
          ,INTO_TITLE
            ,INTO_PHONE
            ,INTO_PRODUCT
          ,INTO_DATE
          ,INTO_ENTRY
          ,INTO_INFO

          ,INTO_FILENAME

           ,USER_ID
           ,INTO_CITY
           ,INTO_PROGRESS
          )

VALUES ( #{into_no}
      ,#{into_title}
       ,#{into_phone}
         ,#{into_product}
          ,#{into_date}
         ,#{into_entry}
         ,#{into_info}

         ,#{into_filename}

         ,#{user_id}
         ,#{into_city}
         ,0
         )

mapper.update:
UPDATE INTOFARM
SET INTO_PROGRESS=1
WHERE INTO_NO=#{into_no}
