mapper.update:
UPDATE PRODUCT 


	   PRO_NAME = #{pro_name}


	 , PRO_PRICE = #{pro_price}


	 , PRO_DCOMPANY = #{pro_dcompany}


	 , PRO_DCOST = #{pro_dcost}


	 , PRO_QUANTITY = #{pro_quantity}


	 , PRO_CONDITION = #{pro_condition}


	 , PRO_CONTENT = #{pro_content}


	 , USER_ID = #{user_id}


	 , PRO_FILENAME = #{pro_filename}


	 , PRO_CATEGORY = #{pro_category}


 WHERE PRO_NO = #{pro_no}
