mapper.select:
SELECT COUNT(*)
FROM PRODUCT


		AND UPPER(PRO_NAME) LIKE '%' || UPPER(#{pro_name}) || '%'
		OR UPPER(PRO_CONTENT) LIKE '%' || UPPER(#{pro_content}) || '%'


		OR PRO_CATEGORY = #{pro_category}


		OR PRO_PRICE <= #{pro_price}

SELECT B.* FROM (SELECT ROWNUM RN, A.* FROM ( SELECT PRO_NO , PRO_NAME , TO_CHAR(PRO_PRICE, '999,999,999') PRO_PRICE , PRO_DCOMPANY , PRO_QUANTITY , PRO_CONDITION , PRO_CONTENT , USER_ID , PRO_FILENAME , PRO_CATEGORY FROM PRODUCT
AND UPPER(PRO_NAME) LIKE '%' || UPPER(#{pro_name}) || '%' OR UPPER(PRO_CONTENT) LIKE '%' || UPPER(#{pro_content}) || '%'
OR PRO_CATEGORY = #{pro_category}
OR PRO_PRICE <= #{pro_price}
ORDER BY
PRO_QUANTITY PRO_PRICE PRO_PRICE DESC PRO_NO DESC
) A) B WHERE RN BETWEEN #{start} AND #{end}

SELECT B.* FROM (SELECT ROWNUM RN, A.* FROM ( SELECT PRO_NO , PRO_NAME , TO_CHAR(PRO_PRICE, '999,999,999') PRO_PRICE , PRO_DCOMPANY , PRO_QUANTITY , PRO_CONDITION , PRO_CONTENT , USER_ID , PRO_FILENAME , PRO_CATEGORY FROM PRODUCT
USER_ID = #{user_id}
ORDER BY PRO_NO DESC ) A) B WHERE RN BETWEEN #{start} AND #{end}

mapper.insert:

	SELECT NVL(MAX(PRO_NO), 0) + 1 FROM PRODUCT

INSERT INTO PRODUCT (PRO_NO
   		  	   	   , PRO_NAME
	      	  	   , PRO_PRICE
	 			   , PRO_DCOMPANY
	  			   , PRO_QUANTITY
	  			   , PRO_CONDITION
				   , PRO_CONTENT
				   , USER_ID
				   , PRO_FILENAME
				   , PRO_CATEGORY)
			  VALUES(#{pro_no}
				   , #{pro_name}
				   , #{pro_price}
				   , #{pro_dcompany}
				   , #{pro_quantity}
				   , #{pro_condition}
				   , #{pro_content}
				   , #{user_id}
				   , #{pro_filename}
				   , #{pro_category})

mapper.update:
UPDATE PRODUCT


		PRO_NAME = #{pro_name}


		, PRO_PRICE = #{pro_price}


		, PRO_DCOMPANY = #{pro_dcompany}


		, PRO_QUANTITY = #{pro_quantity}


		, PRO_CONDITION = #{pro_condition}


		, PRO_CONTENT = #{pro_content}


		, USER_ID = #{user_id}


		, PRO_FILENAME = #{pro_filename}


		, PRO_CATEGORY = #{pro_category}


WHERE PRO_NO = #{pro_no}

