   SET CART_COUNT = #{cart_count}
 WHERE PRO_NO = #{pro_no}

mapper.select:
SELECT COUNT(*)
  FROM CART
 WHERE USER_ID = #{user_id}
   AND PRO_NO = #{pro_no}

