mapper.select:
SELECT COUNT(*)
FROM PRODUCT


		AND UPPER(PRO_NAME) LIKE '%' || UPPER(#{pro_name}) || '%'
		OR UPPER(PRO_CONTENT) LIKE '%' || UPPER(#{pro_content}) || '%'


		OR PRO_CATEGORY = #{pro_category}


		OR PRO_PRICE <= #{pro_price}

SELECT B.* FROM (SELECT ROWNUM RN, A.* FROM ( SELECT PRO_NO , PRO_NAME , TO_CHAR(PRO_PRICE, '999,999,999') PRO_PRICE , PRO_DCOMPANY , TO_CHAR(PRO_DCOST, '999,999,999') PRO_DCOST , PRO_QUANTITY , PRO_CONDITION , PRO_CONTENT , USER_ID , PRO_FILENAME , PRO_CATEGORY FROM PRODUCT
AND UPPER(PRO_NAME) LIKE '%' || UPPER(#{pro_name}) || '%' OR UPPER(PRO_CONTENT) LIKE '%' || UPPER(#{pro_content}) || '%'
OR PRO_CATEGORY = #{pro_category}
OR PRO_PRICE <= #{pro_price}
ORDER BY
PRO_QUANTITY PRO_PRICE PRO_PRICE DESC PRO_NO
) A) B WHERE RN BETWEEN #{start} AND #{end}
