mapper.select:
SELECT B.* FROM (SELECT ROWNUM RN, A.* FROM ( SELECT USER_ID, USER_PWD, USER_NAME, USER_EMAIL, USER_ZIP, USER_BIRTH, USER_PHONE, USER_ADR, USER_DETAILADR, USER_AUTH, CASE USER_AUTH WHEN 'farmer' THEN 'O' ELSE 'X' END AS FARMER_CHECK FROM USERS
NOT USER_AUTH IN 'admin'
AND USER_AUTH = #{user_auth}
) A) B WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE USERS
USER_PWD=#{user_pwd}, USER_NAME=#{user_name}, USER_EMAIL=#{user_email}, USER_BIRTH=#{user_birth}, USER_PHONE=#{user_phone}, USER_ZIP=#{user_zip}, USER_ADR=#{user_adr}, USER_DETAILADR=#{user_detailadr}
WHERE USER_ID = #{user_id}

UPDATE USERS
   SET USER_AUTH = #{user_auth}
 WHERE USER_ID = #{user_id}

UPDATE FARMER
   SET FARMER_CHECK = #{farmer_check}
 WHERE USER_ID = #{user_id}
